mapper.update:
UPDATE t_time_entry 
SET date=#{date}
, type_id=#{typeId}
, comment=#{comment}
, duration=#{duration}
, update_by=#{updateBy}
where id=#{id}

mapper.select:
SELECT SUM(duration)
FROM t_time_entry 
WHERE date = #{date}

SELECT *
FROM t_time_entry
WHERE 1 = 1

	and date >= #{dateFrom}


	and date <= #{dateTo}


	and type_id = #{typeId}
